export class BurgerMenu{constructor(){this.menues=[],this.is_open=!1}init(){document.querySelectorAll("[data-burger]").forEach((e=>{const s=e.querySelector(".menu__close-button");this.menues.push({burger:e,close_btn:s}),s.addEventListener("click",(()=>{this.is_open?this.close(e,s):this.open(e,s)}));e.querySelectorAll('a[href^="#"]').forEach((t=>{t.addEventListener("click",(t=>{this.is_open&&setTimeout((()=>{this.close(e,s)}),100)}))}))}))}open(e,s){this.is_open=!0,e.classList.add("_open"),document.body.classList.add("_locked"),s.setAttribute("aria-label","Закрити меню-бургер")}close(e,s){this.is_open=!1,document.body.classList.remove("_locked"),e.classList.remove("_open"),s.setAttribute("aria-label","Відкрити меню-бургер")}}