export class BurgerMenu{constructor(){this.menues=[],this.is_open=!1}init(){document.querySelectorAll("[data-burger]").forEach((e=>{const t=e.querySelector(".menu__close-button");this.menues.push({burger:e,close_btn:t}),t.addEventListener("click",(()=>{this.is_open?this.close(e,t):this.open(e,t)}));e.querySelectorAll('a[href^="#"]').forEach((s=>{s.addEventListener("click",(()=>{this.close(e,t)}))}))}))}open(e,t){this.is_open=!0,e.classList.add("_open"),document.body.classList.add("_locked"),t.setAttribute("aria-label","Закрити меню-бургер");const s=e.querySelector("a, button, input, [tabindex]:not([tabindex='-1'])");s?.focus()}close(e,t){this.is_open=!1,e.classList.remove("_open"),document.body.classList.remove("_locked"),t.setAttribute("aria-label","Відкрити меню-бургер")}}