export class Ligth{constructor(){this.lights=[]}init(){document.querySelectorAll("[data-light]").forEach((t=>{this.lights.push({el:t,parent:document.getElementById(t.dataset.light.split("|")[0]),sub_parent:t.dataset.light.split("|").length>1?document.getElementById(t.dataset.light.split("|")[1]):null})})),this.setPlase(this.lights),window.addEventListener("resize",(t=>{this.setPlase(this.lights)}))}setPlase(t){t.forEach((t=>{const e=t.parent.offsetTop,s=t.parent.offsetHeight*t.el.dataset.lightYOffset,l=t.sub_parent?t.sub_parent.offsetLeft:0;t.el.style.top=`${e+s}px`,l>0&&(t.el.style.right=`${l}px`)}))}}